import logo from './logo.svg';
import './App.css';
import Display from './components/Display/Display.js';
import Input from './components/Input/Input.js';
import { useState, useEffect } from "react";
import frog from './frog.png';
import axios from 'axios';

function App(props) {
  const REST_URL = 'http://localhost:8080/api/customers';

  let blankCustomer = { "id": -1, "name": "", "email": "", "password": "" };

  const [formObject, setFormObject] = useState(blankCustomer);
  const [customers, setCustomers] = useState([]);
  const [refresh, setRefresh] = useState(false); // State to trigger re-fetch

  let mode = (formObject.id >= 0) ? 'Update' : 'Add';

  useEffect(() => { 
    getCustomers(); 
  }, [refresh]); // Run when `refresh` changes

  const getNextId = (people) => {
    let maxId = 0;
    for (let item of people) {
      maxId = (item.id > maxId) ? item.id : maxId;
    }
    return maxId + 1;
  };

  let nextId;

  const getCustomers = async () => {
    try {
      const response = await axios.get(`${REST_URL}`);
      setCustomers(response.data);
      nextId = getNextId(response.data);
    } catch (error) {
      console.error("error fetching customers: ", error);
    }
  };

  const handleInputChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    let newFormObject = { ...formObject };
    newFormObject[name] = value;
    setFormObject(newFormObject);
  };

  const handleListClick = (item) => {
    if (formObject.id === item.id) {
      setFormObject(blankCustomer);
    } else {
      setFormObject(item);
    }
  };

  const onSaveClick = async () => {
    try {
      if (mode === 'Add') {
        formObject.id = nextId;
        await axios.post(`${REST_URL}`, formObject);
      } else if (mode === 'Update') {
        await axios.put(`${REST_URL}/${formObject.id}`, formObject);
      }
      setFormObject(blankCustomer);
      // Trigger re-fetch of customers
      setRefresh(!refresh);
    } catch (error) {
      console.error("error saving customer: ", error);
    }
  };

  const onCancelClick = () => {
    setFormObject(blankCustomer);
  };

  const onDeleteClick = async () => {
    if (formObject.id >= 0) {
      try {
        await axios.delete(`${REST_URL}/${formObject.id}`);
        setFormObject(blankCustomer);
        // Trigger re-fetch of customers
        setRefresh(!refresh);
      } catch (error) {
        console.error("error deleting customer: ", error);
      }
    }
  };

  let pvars = {
    mode: mode,
    handleInputChange: handleInputChange,
    formObject: formObject,
    onDeleteClick: onDeleteClick,
    onSaveClick: onSaveClick,
    onCancelClick: onCancelClick
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="pageTitle">
          Welcome to the CUSTOMER LOOKUP TABLE WHERE YOU CAN DO CUSTOMER LOOKUP FOR LOOKING UP CUSTOMERS IN THE CUSTOMER LOOKUP TABLE
        </div>
        <div className="pageTitle">
          Welcome to the CUSTOMER LOOKUP TABLE WHERE YOU CAN DO CUSTOMER LOOKUP FOR LOOKING UP CUSTOMERS IN THE CUSTOMER LOOKUP TABLE
        </div>
      </header>
      <Display 
        customers={customers}
        formObject={formObject}
        handleListClick={handleListClick}
      />
      <Input {...pvars}/>
      <footer>
        <img id="frog" src={frog} alt="frog"/>
      </footer>
    </div>
  );
}

export default App;
